mapper.select:
SELECT a.id,a.image,a.serialNumber,a.department,a.repairStaff,a.content, a.cellPhone,a.assetNumber,a.assetSn,a.orderPeople,a.assistants, a.type as typeId,a.status as statusId,a.registerPeople,a.registerDate, a.modifyPeople,a.modifyDate,b.type,c.status,d.depName,d.depPhone, e.name as jiedan,f.name as baoxiu,g.finishDate FROM t_repair_register a join t_repair_type b on a.type = b.id join t_repair_status c on a.status = c.id join t_department_info d on a.department = d.id left join t_member_info e on a.orderPeople = e.number left join t_member_info f on a.repairStaff = f.number join t_order_register g on a.serialNumber = g.serialNumber
and (a.serialNumber = #{pd.keyword} or d.depName LIKE CONCAT(CONCAT('%', #{pd.keyword}),'%') )
and a.registerDate >= #{pd.startTime}
and a.registerDate <= #{pd.endTime}
and a.orderPeople = #{pd.orderPeople}
order by a.registerDate desc

SELECT a.id,a.image,a.serialNumber,a.department,a.repairStaff,a.content, a.cellPhone,a.assetNumber,a.assetSn,a.orderPeople,a.assistants, a.type as typeId,a.status as statusId,a.registerPeople,a.registerDate, a.modifyPeople,a.modifyDate,b.type,c.status,d.depName,d.depPhone, e.name as jiedan,f.name as baoxiu,g.finishDate FROM t_repair_register a join t_repair_type b on a.type = b.id join t_repair_status c on a.status = c.id join t_department_info d on a.department = d.id left join t_member_info e on a.orderPeople = e.number left join t_member_info f on a.repairStaff = f.number join t_order_register g on a.serialNumber = g.serialNumber
and ( a.serialNumber = #{keyword} or d.depName LIKE CONCAT(CONCAT('%', #{pd.keyword}),'%') )
and a.registerDate >= #{startTime}
and a.registerDate <= #{endTime}
and a.orderPeople = #{orderPeople}
order by a.registerDate desc

select a.name
from t_member_info a
where a.number =#{id}
